DROP TABLE IF EXISTS `code`;
CREATE TABLE `code` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `phone` varchar(20) DEFAULT NULL COMMENT '手机号',
  `code` varchar(10) DEFAULT NULL COMMENT '验证码',
  `send_time` datetime DEFAULT NULL COMMENT '生效时间',
  `num` tinyint(4) DEFAULT NULL COMMENT '发送次数',
  `type` tinyint(4) DEFAULT NULL COMMENT '验证码类型 0-借阅 1-归还 2-报失',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 COMMENT='验证码';

